import { Container, Typography, Button, Box, Grid, Paper } from "@mui/material";
import { motion } from "framer-motion";
import { useRouter } from "next/router";

export default function Home() {
  const router = useRouter();

  return (
    <Container maxWidth="md" sx={{ textAlign: "center", py: 8 }}>
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
        <Typography variant="h3" fontWeight="bold" gutterBottom>
          Matrix Calculator
        </Typography>
        <Typography variant="h6" color="text.secondary" gutterBottom>
          Solve complex matrix operations easily with our interactive calculator.
        </Typography>
      </motion.div>

      <Grid container spacing={4} justifyContent="center" sx={{ mt: 4 }}>
        <Grid item xs={12} sm={6}>
          <Paper elevation={6} sx={{ p: 3, borderRadius: 4 }}>
            <Typography variant="h5" gutterBottom>
              Perform Matrix Operations
            </Typography>
            <Typography variant="body1" color="text.secondary">
              Add, subtract, multiply, and find determinants with ease.
            </Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Paper elevation={6} sx={{ p: 3, borderRadius: 4 }}>
            <Typography variant="h5" gutterBottom>
              Instant Results
            </Typography>
            <Typography variant="body1" color="text.secondary">
              Get real-time solutions with our fast and efficient algorithm.
            </Typography>
          </Paper>
        </Grid>
      </Grid>

      <Box sx={{ mt: 6 }}>
        <motion.div whileHover={{ scale: 1.1 }}>
          <Button
            variant="contained"
            color="primary"
            size="large"
            sx={{ px: 4, py: 1.5, fontSize: "1.2rem", borderRadius: 4 }}
            onClick={() => router.push("/cms")}
          >
            Start Calculating
          </Button>
        </motion.div>
      </Box>
    </Container>
  );
}
